// API KEYS
const apiKey = "AIzaSyAQQ_GV3xzsZH5Bhki2n-M1_2XvrkWOvKI";
const cxID = "97e47ea83d8d74ae5";

// Các nhóm từ khóa
const tuKhoaChinh = '"Đại học Cửu Long" OR "MKU" OR "DHCL"';
const tuKhoaTieuCuc = '"lừa đảo" OR "gian lận" OR "chiếm đoạt" OR "thiếu minh bạch" OR "phốt" OR "từ ngữ đã kích" OR "mất uy tín" OR "chất lượng kém"';
const tuKhoaLinhVuc = '"tuyển sinh" OR "giảng viên" OR "giáo dục" OR "đào tạo" OR "sinh viên" OR "bằng cấp"';
const site = 'site:vnexpress.net OR site:tuoitre.vn OR site:thanhnien.vn OR site:vietnamnet.vn';

// Ghép query
const query = `(${tuKhoaChinh}) (${tuKhoaTieuCuc}) (${tuKhoaLinhVuc}) (${site})`;

// Tạo URL (có encodeURIComponent)
const url = `https://www.googleapis.com/customsearch/v1?key=${apiKey}&cx=${cxID}&num=10&q=${query}&sort=date&lr=lang_vi&gl=vn`;

// Trả về kết quả
return [{ json: { url } }];

const ketQuaTong = [];

for (const input of items) {
  const danhSach = input.json.items || [];
  for (const kq of danhSach) {
    ketQuaTong.push({
      json: {
        tieuDe: kq.title || '',   
        link: kq.link || '',      
        noiDung: kq.snippet || ''
      }
    });
  }
}
return ketQuaTong;

const Truong = ["đại học cửu long" , "mku" , "dhcl"];
const tuKhoaTieuCuc = ["lừa đảo" , "gian lận" , "chiếm đoạt" , "thiếu minh bạch" , "phốt" , "từ ngữ đã kích" , "mất uy tín" , "chất lượng kém"];

// Lấy thời gian ở VN
const thoiGian = new Date().toLocaleString("vi-VN", {
  timeZone: "Asia/Ho_Chi_Minh",
  hour12: false
});

// Hàm kiểm tra có chứa từ khóa
const coChua = (text, dsTuKhoa) => {
  return dsTuKhoa.some(tu => text.includes(tu));
};

const ketQua = items
  .map(item => {
    const tieuDe = (item.json.tieuDe || "").toLowerCase();
    const noiDung = (item.json.noiDung || "").toLowerCase();
    const vanBan = tieuDe + " " + noiDung;

    if (coChua(vanBan, Truong) && coChua(vanBan, tuKhoaTieuCuc)) {
      const tuKhoaTimThay = tuKhoaTieuCuc.filter(tu => vanBan.includes(tu));
      return {
        json: {
          TieuDe: item.json.tieuDe,
          Link: item.json.link,
          NoiDung: item.json.noiDung,
          TuKhoa: tuKhoaTimThay.join(", "),
          NgayPhatHien: thoiGian
        }
      };
    }
    return null;
  })
  .filter(Boolean);

return ketQua;

const oldArticles = items.filter(i => i.json.row_number);
const newArticles = items.filter(i => !i.json.row_number);


const oldList = oldArticles.map(a => ({
  title: (a.json.TieuDe || "").trim().toLowerCase(),
  content: (a.json.NoiDung || "").substring(0,100).trim().toLowerCase(),
  link: (a.json.Link || "").trim().toLowerCase()
}));


const uniqueArticles = newArticles.filter(a => {
  const title = (a.json.TieuDe || "").trim().toLowerCase();
  const content = (a.json.NoiDung || "").substring(0,100).trim().toLowerCase();
  const link = (a.json.Link || "").trim().toLowerCase();

  return !oldList.some(o =>
    o.title === title ||
    o.content === content ||
    o.link === link
  );
});

return uniqueArticles;

const ketQua = [];

const isEmpty = (val) => !val || val.trim() === '';

const extract = (text, label) => {
  const regex = new RegExp(`${label}\\s*([\\s\\S]*?)(?=\\n\\n|$)`, 'i');
  const match = text.match(regex);
  return match ? match[1].trim() : '';
};

for (const item of items) {
  const data = item.json;

  const allFields = [
    data.tieuDe,
    data.noiDung,
    data.huongXuLy,
    data.mucDoAnhHuong,
    data.output
  ];

  // Nếu tất cả các trường rỗng thì bỏ qua
  if (allFields.every(isEmpty)) continue;

  let tieuDe = data.tieuDe?.trim() || '';
  let noiDung = data.noiDung?.trim() || '';
  let huongXuLy = data.huongXuLy?.trim() || '';
  let mucDoAnhHuong = data.mucDoAnhHuong?.trim() || '';
  let nguonBaiViet = data.nguonBaiViet?.trim() || 'Cần xác minh thêm';

  // Nếu output có dữ liệu thì trích xuất
  if (!isEmpty(data.output)) {
    const out = data.output.trim();
    tieuDe = extract(out, 'Tóm tắt:') || tieuDe;
    noiDung = extract(out, 'Tóm tắt:') || noiDung;
    huongXuLy = extract(out, 'Hướng xử lý:') || huongXuLy;
    mucDoAnhHuong = extract(out, 'Mức độ ảnh hưởng:') || mucDoAnhHuong;
    nguonBaiViet = extract(out, 'Dự đoán nguồn bài viết:') || nguonBaiViet;
  }

  // Nếu tất cả các trường quan trọng đều rỗng thì bỏ qua
  if ([tieuDe, noiDung, huongXuLy, mucDoAnhHuong].every(isEmpty)) continue;

  ketQua.push({
    json: {
      tieuDe: tieuDe.slice(0, 100),
      noiDung,
      huongXuLy,
      mucDoAnhHuong
    }
  });
}

// Trả về kết quả, có thể là mảng rỗng
return ketQua;

