// Bảng quy đổi mức độ ưu tiên
const uuTien = { "Cao": 3, "Trung": 2, "Thấp": 1 };

// Hàm chuẩn hóa mức độ ảnh hưởng
function chuanHoaMucDo(text) {
  if (!text) return "Thấp"; // Mặc định là Thấp nếu không có
  const muc = text.trim().split(" ")[0];
  if (text.startsWith("Cần xác minh thêm")) return "Thấp";
  return muc;
}

// Xử lý dữ liệu đầu vào
const ketQua = $input.all()
  .map(item => {
    const mucDo = chuanHoaMucDo(item.json["MucDoAnhHuong"]);
    return {
      ...item.json,
      MucDoAnhHuong: mucDo
    };
  })
  .sort((a, b) => {
    const aMuc = uuTien[a.MucDoAnhHuong] || 0;
    const bMuc = uuTien[b.MucDoAnhHuong] || 0;
    // Sắp xếp theo mức độ trước, sau đó theo ngày (mới nhất trước)
    if (bMuc !== aMuc) return bMuc - aMuc;
    return new Date(b.NgayPhatHien) - new Date(a.NgayPhatHien);
  })
  .map((item, index) => ({
    json: {
      STT: index + 1,
      Tiêu_đề: item.TieuDe || "Không rõ",
      Nội_dung: item.NoiDungTomTat || "Không rõ",
      Hướng_xử_lý: item.HuongXuLy || "Không rõ",
      Mức_độ: item.MucDoAnhHuong || "Không rõ",
      Liên_kết: item.LienKet || "Không rõ",
      Ngày_phát_hiện: item.NgayPhatHien || "Không rõ"
    }
  }));

return ketQua;

// Hàm chuẩn hóa mức độ
function chuanHoaMucDo(text) {
  if (!text) return null; // Không có dữ liệu thì bỏ qua
  if (text.startsWith("Cần xác minh thêm")) return null; // Loại bỏ
  if (text.startsWith("Trung")) return "Trung bình";
  if (text.startsWith("Thấp")) return "Thấp";
  if (text.startsWith("Cao")) return "Cao";
  return null;
}

// Đếm số bài viết theo mức độ
let cao = 0, trung = 0, thap = 0;

for (const item of $input.all()) {
  const muc = chuanHoaMucDo(item.json["Mức_độ"]);
  if (muc === "Cao") cao++;
  else if (muc === "Trung bình") trung++;
  else if (muc === "Thấp") thap++;
}

const tong = cao + trung + thap;

// Lấy ngày hiện tại theo giờ Việt Nam (UTC+7)
const now = new Date();
now.setUTCHours(now.getUTCHours() + 7);

// Tính ngày đầu tuần (thứ 2) và cuối tuần (chủ nhật)
const dayOfWeek = now.getDay(); // Chủ nhật = 0
const monday = new Date(now);
monday.setDate(now.getDate() - (dayOfWeek === 0 ? 6 : dayOfWeek - 1));
const sunday = new Date(monday);
sunday.setDate(monday.getDate() + 6);

// Định dạng ngày YYYY-MM-DD
const formatDate = (d) => d.toISOString().split("T")[0];

// Xuất dữ liệu thống kê
return [{
  json: {
    "ID": 1,
    "Từ ngày": formatDate(monday),
    "Đến ngày": formatDate(sunday),
    "Cao": cao,
    "Trung bình": trung,
    "Thấp": thap,
    "Tổng": tong
  }
}];

